import React from "react";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Wally from "../images/wally3.png";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    overflow: "hidden",
    padding: theme.spacing(0, 3),
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "70ch",
    },
  },
  paper: {
    maxWidth: 500,
    margin: `${theme.spacing(1)}px auto`,
    padding: theme.spacing(2),
  },
}));

const title = `Title `;
const forum = `This will be the posts we need to make a function to create a <p> tag inside for (huge array) 
Change this to list items`;
const search = `Truncation should be conditionally applicable on this long line of text
 as this is a much longer line than what the container can support. `;

const Forum = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="stretch"
        spacing={5}
      >
        <Grid item>
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={Wally}
                title="Wally"
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  Watercooler Forum
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  Need to add text her with instructions how to use the forum
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size="small" color="primary">
                Share
              </Button>
              <Button size="small" color="primary">
                Learn More
              </Button>
            </CardActions>
          </Card>
        </Grid>
      </Grid>
      <Paper className={classes.paper}>
        <Grid container wrap="nowrap" spacing={2}>
          <Grid item></Grid>
          <Grid item xs={8}>
            <Typography>{forum}</Typography>
          </Grid>
        </Grid>
      </Paper>
      <Paper className={classes.paper}>
        <Grid container wrap="nowrap" spacing={2}>
          <Grid item>
            <Button variant="outlined" color="primary">
              Submit Post
            </Button>
          </Grid>
          <Grid item xs>
            <TextField
              id="outlined-multiline-static"
              label="Create Post!"
              multiline
              rows={6}
              defaultValue="What do you hate about work?"
              variant="outlined"
            />
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
};

export default Forum;
